import './App.css';
import Button from './components/Button';
import { useContext } from 'react';
import { ThemeContext } from './contexts/theme-context';
import { UserContext } from './contexts/user-context';

function App() {

  const { user, setUser, clearUser } = useContext(UserContext);

  const { variant, setVariant, theme } = useContext(ThemeContext);

  const handleOnClick = (name) => {
    setUser({ name: name })
  }

  const handleAddUser = ({target: value}) => {
    setUser({ name: value.form[0].value})
    console.log(value.form[0].value);
  }

  const handleOnClickTheme = () => {
    setVariant(variant === 'light' ? 'dark' : 'light')
  }

  const handleOnClickClear = () => {
    clearUser();
  }


  return (
    <div className="App"
      style={
        {
          backgroundColor: theme.background,
          color: theme.color
        }}>
      <header className="App-header">
        <Button onClick={handleOnClickTheme}>Click me to change theme</Button>
        <Button onClick={handleOnClickClear}>Click me to clear user</Button>
        {user && <Button onClick={handleOnClick}>{user.name}</Button>}
        <form>
          <label>Enter your name</label>
          <input placeholder='type name' name='name' id='name'/>
          <button type='submit' onClick={handleAddUser}>Submit</button>
        </form>
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
