import { createContext, useState } from 'react';
import themeData from './theme';

const ThemeContext = createContext(undefined);
const ThemeContextDispatchers = createContext(undefined);

const ThemeProvider = ({ children, theme }) => {
    const [variant, setVariant] = useState(themeData.light);
    return (
        <ThemeContext.Provider value={variant}>
            <ThemeContextDispatchers.Provider value={setVariant}>
                {children}
            </ThemeContextDispatchers.Provider>
        </ThemeContext.Provider>

    )
}


export default { ThemeContext, ThemeContextDispatchers, ThemeProvider };
