import { useContext, useMemo } from "react";
import { UserContext } from "../contexts/user-context";
import Button from "../components/Button";

const User = () => {

    const { user, setUser, clearUser } = useContext(UserContext);

    const handleClearUser = () => {
        clearUser();
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        setUser({ name: event.tager[0].value, email: event.target[1].value, age: event.target[2].value })
    }

    const userData = useMemo((user) => (
        <ul>
            <li>Name:{user.name}</li>
            <li>Email: {user.email}</li>
            <li>Age: {user.age}</li>
        </ul>
    ), [user]);


    return (
        <div className="user-container">
            <h1>User</h1>
            {user && userData}
            <form onSubmit={handleSubmit}>
                <label>Enter user name</label>
                <input placeholder="type name here" name="name" id="name" />
                <label>Enter your email</label>
                <input placeholder="type email here" name="email" id="email"></input>
                <label>Enter your age</label>
                <input placeholder="type age here" name="age" id="age"></input>
                <Button type='submit'>Add user</Button>
            </form>
            <Button onClick={handleClearUser}>Click me to clear user</Button>
        </div>
    )
}
export default User;
